import logo from './logo.svg';
import './App.css';
import socket from './socket';
import { useEffect, useState } from 'react';

function App() {
  const [username, setUsername] = useState('');
  const sessionID = sessionStorage.getItem("sessionID");

  if (sessionID) {
    console.log('running it');
    socket.auth = { sessionID };
    socket.connect();
  }

  const onUsernameSubmission = (username) => {
    socket.connect();

    socket.auth = { username };
    socket.connect();
  }
  useEffect(() => {
    socket.onAny((event, ...args) => {
      console.log(event, args);
    });

    socket.on('session', (sessionInformation)=> {
      console.log(sessionInformation);

      sessionStorage.setItem("sessionId", sessionInformation.sessionID.toString());
      sessionStorage.setItem("userID", sessionInformation.userID.toString());
    })

  }, [])



  return (
    <div className = "h-screen min-h-screen flex justify-center">
      <div className="flex flex-col justify-center items-center content-around">
          <input className='border' type="text" value={username} onChange={e => setUsername(e.target.value)}/>
          <button className = 'border' onClick={() => onUsernameSubmission(username)}>Click mhe!</button>
      </div>              
    </div>  
);
}

export default App;
