import './../App.css';
import { useEffect, useState } from 'react';
import socket from '../socket';

const UsernameComponent = ({ sessionId, setSessionId, userID, setUserID }) => {
    const [inputValue, setInputValue] = useState("");
    const [errText, setErrorText] = useState("");
    const [match, setMatch] = useState();
    const onChangeHandler = event => {
        setInputValue(event.target.value);
      };

      const createLobby = (e) => {
        e.preventDefault();
        socket.emit("create-room", inputValue, cb => {
          if(!cb){
            setErrorText("Invalid Lobby Id");
            return;
          }
          socket.once("player-joined", roomInfo => {
            setMatch(roomInfo);
          })

          console.log(cb);
        });

      }

      const joinLobby = (e) => {
        socket.emit("join-room", inputValue, cb => {
          if(cb != false){
            setMatch(cb);
          }
        })
      }
    return (
    <div className='flex flex-col justify-center h-screen  w-6/12 mr-auto'>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={createLobby}>Create Lobby</button>
        </div>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={joinLobby}>Join Lobby {socket.connected}</button>
          {errText}
        </div>
      </div>
    );
}

export default UsernameComponent;
