import React, { useEffect, useState } from 'react';
import UsernameComponent from './components/usernameComponent';
import { useSocket } from './socket';

function App() {
  const socket = useSocket();
  const [sessionId, setSessionId] = useState(sessionStorage.getItem("sessionId"));
  const [userID, setUserID] = useState(sessionStorage.getItem("userID"));
  const [inputValue, setInputValue] = useState("");
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    if (userID && socket) {
      const name = 'jamie';
      socket.auth = { name };
      socket.connect();
    }
  }, [userID]);

  useEffect(() => {
    const handleConnect = () => {
      setConnected(true);
      console.log("Socket connected woo");
    };

    socket.on('connect', handleConnect);

    return () => {
      socket.off('connect', handleConnect);
    };
  }, [socket]);

  const onChangeHandler = event => {
    setInputValue(event.target.value);
  };

  const createLobby = () => {
    socket.emit("Create-Room", inputValue, reply => {
      console.log(reply);
    });
  };

  return (
    <>
      {userID == null && sessionId == null && (
        <UsernameComponent
          sessionId={sessionId}
          setSessionId={setSessionId}
          userID={userID}
          setUserID={setUserID}
        />
      )}
      <div className='flex flex-col justify-center h-screen w-6/12 mr-auto'>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={createLobby}>Create Lobby</button>
        </div>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button>Join Lobby</button>
        </div>
      </div>
    </>
  );
}

export default App;
