import { useEffect, useState } from 'react';
import UsernameComponent from './components/usernameComponent';
import socket from './socket';


function App() {
  const [sessionID, setSessionId] = useState(sessionStorage.getItem("sessionId"));
  const [userID, setUserID] = useState(sessionStorage.getItem("userID"));
  const [room, setRoom] = useState('');
  const [inputValue, setInputValue] = useState("");
  const [errText, setErrorText] = useState("");

  useEffect(() => {
    if (sessionID) {
      socket.auth = { sessionID };
      socket.connect();
      console.log(socket);
    }


  }, []);

  socket.on('connect', function () {
    console.log(socket);
  });
  socket.on('session', ({sessionID, userID, username})=> {
    sessionStorage.setItem("sessionId", sessionID);
    sessionStorage.setItem("userID", userID);
    setSessionId(parseInt(sessionID)); // Parse to integer
    setUserID(parseInt(userID)); // Parse to integer
  });
  const onChangeHandler = event => {
    setInputValue(event.target.value);
  };

  const createLobby = (e) => {
    e.preventDefault();
    socket.emit("create-room", inputValue, cb => {
      if(!cb){
        setText("Invalid Lobby Id");
      }
    });

  }

  return (
    <>
      {userID == null && sessionID == null && <UsernameComponent sessionId={sessionID} setSessionId={setSessionId} userID={userID} setUserID={setUserID} />}
      <div className='flex flex-col justify-center h-screen  w-6/12 mr-auto'>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={createLobby}>Create Lobby</button>
        </div>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button>Join Lobby {socket.connected}</button>
          errText
        </div>
      </div>
    </>
  );
}

export default App;
