import './../App.css';
import { useEffect, useState } from 'react';
import socket from '../socket';

const Card = ({cardValue, cardName, cardID }) => {

    const selectCard = () => {
        socket.emit("chosen-card", cardID);
    }


    return (
        <>
            <div onClick={selectCard}>{cardName}</div>
            <div onClick={selectCard}>{cardValue}</div>
        </>
    );
}

export default Card;
