import React, { useEffect, useState } from 'react';
import UsernameComponent from './components/usernameComponent';
import { io } from "socket.io-client";

function App() {

  const URL = "http://localhost:3000";
  const socket = io(URL, { autoConnect: false }); // Disable autoConnect



  const [sessionId, setSessionId] = useState(sessionStorage.getItem("sessionId"));
  const [userID, setUserID] = useState(sessionStorage.getItem("userID"));
  const [inputValue, setInputValue] = useState("");
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const handleConnect = () => {
      console.log("Socket connected woo");
      setConnected(true);
    };

    const handleSession = (sessionInformation) => {
      console.log(sessionInformation);
      sessionStorage.setItem("sessionId", sessionInformation.sessionID);
      sessionStorage.setItem("userID", sessionInformation.userID);
      setSessionId(sessionInformation.sessionID); // Store as string
      setUserID(sessionInformation.userID); // Store as string
    };

    socket.on('connect', handleConnect);
    socket.on('session', handleSession);

    // Attempt connection if userID is already available
    if (userID) {
      const name = 'jamie';
      socket.auth = { name };
      socket.connect();
    }

    return () => {
      socket.off('connect', handleConnect);
      socket.off('session', handleSession);
    };
  }, [userID]);

  const onChangeHandler = event => {
    setInputValue(event.target.value);
  };

  const createLobby = () => {
    socket.emit("Create-Room", inputValue, reply => {
      console.log(reply);
    });
  };
  socket.connect();
  console.log(socket);
  return (
    <>
      {userID == null && sessionId == null && (
        <UsernameComponent
          sessionId={sessionId}
          setSessionId={setSessionId}
          userID={userID}
          setUserID={setUserID}
          socket = {socket}
        />
      )}
      <div className='flex flex-col justify-center h-screen w-6/12 mr-auto'>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={createLobby}>Create Lobby</button>
        </div>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button>Join Lobby</button>
        </div>
      </div>
    </>
  );
}

export default App;
