import { useEffect, useState } from 'react';
import UsernameComponent from './components/usernameComponent';
import MatchMaking from './components/matchMaking';
import socket from './socket';

function App() {
  const [sessionID, setSessionId] = useState(sessionStorage.getItem("sessionId"));
  const [userID, setUserID] = useState(sessionStorage.getItem("userID"));
  const [room, setRoom] = useState('');
  const [reconnectObj, setReconnect] = useState(null);

  useEffect(() => {
    if (sessionID) {
      socket.auth = { sessionID };
      socket.connect();
      console.log(socket);
    }

    socket.on('connect', () => {
      console.log(socket);
    });

    socket.on('reconnected-room', (roomData) => {
      setReconnect(roomData);
    });

    socket.on('session', ({ sessionID, userID, username }) => {
      sessionStorage.setItem("sessionId", sessionID);
      sessionStorage.setItem("userID", userID);
      setSessionId(parseInt(sessionID)); // Parse to integer
      setUserID(parseInt(userID)); // Parse to integer
    });

    // Clean up the event listeners on component unmount
    return () => {
      socket.off('connect');
      socket.off('reconnected-room');
      socket.off('session');
    };
  }, [sessionID]);

  return (
    <>
      {userID == null && sessionID == null && (
        <UsernameComponent
          sessionId={sessionID}
          setSessionId={setSessionId}
          userID={userID}
          setUserID={setUserID}
        />
      )}
      {reconnectObj && reconnectObj.room && (
        <MatchMaking reconnect={reconnectObj} />
      )}
    </>
  );
}

export default App;
