import { useEffect, useState } from 'react';
import UsernameComponent from './components/usernameComponent';
import socket from './socket';


function App() {
  const [sessionId, setSessionId] = useState(parseInt(sessionStorage.getItem("sessionId")) || null);
  const [userID, setUserID] = useState(parseInt(sessionStorage.getItem("userID")) || null);
  const [room, setRoom] = useState('');
  const [inputValue, setInputValue] = useState("");
  socket.connect();
  useEffect(() => {
    if (userID) {
      socket.auth = { sessionId };
      socket.connect();
      console.log("Socket connected woo");
    }
  }, [sessionId]);

  const onChangeHandler = event => {
    setInputValue(event.target.value);
  };

  const createLobby = () => {
    console.log(socket);
    socket.emit("Create-Room", inputValue, reply => {
      console.log(reply);

    });
  }

  return (
    <>
      {userID == null && sessionId == null && <UsernameComponent socket={socket} sessionId={sessionId} setSessionId={setSessionId} userID={userID} setUserID={setUserID} />}
      <div className='flex flex-col justify-center h-screen  w-6/12 mr-auto'>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button onClick={createLobby}>Create Lobby</button>
        </div>
        <div className='flex flex-row justify-evenly items-center'>
          <input type="text" className='border' onChange={onChangeHandler}></input>
          <button>Join Lobby</button>
        </div>
      </div>
    </>
  );
}

export default App;
