import './../App.css';
import { useEffect, useState } from 'react';
import socket from '../socket';

const Card = ({ cardValue, cardName, cardID }) => {

    const selectCard = () => {
        socket.emit("chosen-card", cardID);
        console.log(cardID);
    }

    return (
        <div 
            onClick={selectCard} 
            className="card-container border border-green-300 w-3/12 min-h-full"
            style={{backgroundImage: `url('/TCG_TEMPLATES/Card_Front_Banner_Up.png')`}}
        >
            <div>{cardName}</div>
            <div>{cardValue}</div>
        </div>
    );
}

export default Card;
